
public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count=1, number=1;

		while(count<=3)
		{
		     while(number<=count)
		        {  System.out.print(number+" ");
		               number++;
		}
		     System.out.println();	
		         count++;
		}
	}

}
